import { useRef, useState } from 'react'
import { useHistory } from 'react-router-dom'

import { useAuth } from '../components/Auth'
import { Container, Navbar, Nav, Form, Button } from 'react-bootstrap';


export function Login() {
  const emailRef = useRef()
  const passwordRef = useRef()

  const [error, setError] = useState(null)

  const { signIn } = useAuth()
  const history = useHistory()

  async function handleSubmit(e) {
    e.preventDefault()

    const email = emailRef.current.value
    const password = passwordRef.current.value

    const { error } = await signIn({ email, password })

    if (error) {
      alert('error logging in')
    } else {

      history.push('/overview')
    }
  }

  return (
      <>
        <Navbar bg="dark" variant="dark">
          <Container>
            <Navbar.Brand href="#home">Navbar</Navbar.Brand>
            <Nav className="me-auto">
              <Nav.Link href="#predictor">Predictor</Nav.Link>

            </Nav>
          </Container>
        </Navbar>


        <Container>
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3 mt-3" controlId="email">
              <Form.Label>Email Adresse eingeben</Form.Label>
              <Form.Control type="email" placeholder="name@example.com" ref={emailRef} />
            </Form.Group>
            <Form.Group className="mb-3" controlId="password">
              <Form.Label>Passwort eingeben</Form.Label>
              <Form.Control type="password" placeholder="Passwort" ref={passwordRef} />
            </Form.Group>
            <Form.Group>
              <Button variant="secondary" type="submit"  >Einloggen</Button>

            </Form.Group>
          </Form>
          <p>
            Noch nicht registriert? <Button variant="secondary" href='/signup'  >Hier registrieren</Button>
          </p>
        </Container>

      </>
  )
}
