import { React, Component } from 'react';
import { supabase } from '../components/supabaseClient'
import 'bootstrap/dist/css/bootstrap.min.css'
import 'bootstrap/dist/js/bootstrap.bundle.min.js'

import { Container, Row, Col, Accordion, Table, Button, InputGroup, FormControl } from 'react-bootstrap';

export function FinancialOverview() {
    const data = fetchFinancialData_forUser(supabase.auth.user().id)


}


async function createCategory() {
    const { data, error } = await supabase
    .from('income_forms')
    .insert([
        { /*title: this.state.value, user_id: 1*/ }
    ])
}

async function fetchIncomeForms_forUser(user_id) {
    const { data, error } = await supabase
    .from("income_forms")
    .select()
    .eq("user_id", user_id)
    return data
}

async function fetchIncomes_forIncomeForms(income_forms_id) {
    const { data, error } = await supabase
    .from("incomes")
    .select()
    .eq("income_forms_id", income_forms_id)
    return data
}

async function fetchSpendingForms_forUser(user_id) {
    const { data, error } = await supabase
    .from("spending_forms")
    .select()
    .eq("user_id", user_id)
    return data
}

async function fetchSpendings_forSpendingForms(spending_forms_id) {
    const { data, error } = await supabase
    .from("spendings")
    .select()
    .eq("spending_forms_id", spending_forms_id)
    return data
}

async function fetchFinancialData_forUser(user_id){
    const data_income_forms = await fetchIncomeForms_forUser(user_id)
    const incomes_array = await Promise.all(data_income_forms.map(async income_form => {
        await fetchIncomes_forIncomeForms(income_form.id)
    }))
    
    return [data_income_forms, incomes_array]
}


function insert_accordion(form) {
    return(
        <Accordion.Item eventKey={form.id}>
            <Accordion.Header>{form.title}</Accordion.Header>
            <Accordion.Body>
                <InputGroup>
                    <FormControl type="text" className="bg-light" placeholder="Name" />
                    <FormControl type="text" className="bg-light"  placeholder="Preis" />
                    <Button className="inputField" variant="secondary" /*onClick={() => {this.addTableContent()}}*/>hinzuf√ºgen</Button>
                </InputGroup>
                <Table striped bordered hover variant="dark">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Preis</th>
                        </tr>
                    </thead>
                    <tbody>
                    {
                        //this.state.incomes_or_spendings.map((income_or_spending) => {insert_table(income_or_spending)})
                    }
                    </tbody>
                </Table>
            </Accordion.Body>
        </Accordion.Item>
    );
}

function insert_table(income_entry) {
    return(
        <>
            <tr>
                <td>{income_entry.title}</td>
                <td>{income_entry.amount}</td>
            </tr>
        </>
    );
}
