import { React, Component } from 'react';
import { supabase } from '../components/supabaseClient'
import 'bootstrap/dist/css/bootstrap.min.css'
import 'bootstrap/dist/js/bootstrap.bundle.min.js'

import { Container, Row, Col, Accordion, Table, Button, InputGroup, FormControl } from 'react-bootstrap';

export default async function FinancialOverview() {
    return(
        <Container>
            <p>awd</p>
            <Row>
                <Col>
                    <div className="toptext">
                        <h1 className="text-center">Vermögenseingabe</h1>
                    </div>
                </Col>
            </Row>
            <Row >
                <Col md={6}>
                    <div className="inTitle">
                        <h2 className="text-center">Einnahmen</h2>
                    </div>
                    <div className="inTitle">
                        <InputGroup>
                            <FormControl type="text" className="bg-light" onChange={e => this.setState({ value: e.target.value })} value={this.state.value} />
                            <Button className="inputField" variant="secondary" onClick={() => {this.createCategory()}}>Create Category</Button>
                        </InputGroup>
                    </div>
                    <Accordion>
                        {
                            await fetchFinancialData_forUser("").map((incomeform) => {insert_accordion(incomeform)})
                        }
                    </Accordion>
                </Col>
            </Row>
        </Container>
    );
}


async function createCategory() {
    const { data, error } = await supabase
    .from('income_forms')
    .insert([
        { title: this.state.value, user_id: 1 }
    ])
}

async function fetchIncomeForms_forUser(user_id) {
    const { data, error } = await supabase
    .from("income_forms")
    .select()
    .eq("user_id", user_id)
    if (error) console.log(error)
    if (data) console.log(data)
    return data
}

async function fetchIncomes_forIncomeForms(income_forms_id) {
    const { data, error } = await supabase
    .from("incomes")
    .select()
    .eq("income_forms_id", income_forms_id)
    if (error) console.log(error)
    if (data) console.log(data)
    return data
}

async function fetchSpendingForms_forUser(user_id) {
    const { data, error } = await supabase
    .from("spending_forms")
    .select()
    .eq("user_id", user_id)
    if (error) console.log(error)
    if (data) console.log(data)
    return data
}

async function fetchSpendings_forSpendingForms(spending_forms_id) {
    const { data, error } = await supabase
    .from("spendings")
    .select()
    .eq("spending_forms_id", spending_forms_id)
    if (error) console.log(error)
    if (data) console.log(data)
    return data
}

async function fetchFinancialData_forUser(user_id){
    const data_income_forms = await fetchIncomeForms_forUser(user_id)
    const incomes_array = await Promise.all(data_income_forms.map(async income_form => {
        await fetchIncomes_forIncomeForms(income_form.id)
    }))
    
    return [data_income_forms, incomes_array]
}


function insert_accordion(form) {
    return(
        <Accordion.Item eventKey={form.id}>
            <Accordion.Header>{form.title}</Accordion.Header>
            <Accordion.Body>
                <InputGroup>
                    <FormControl type="text" className="bg-light" placeholder="Name" onChange={e => this.setState({ value: e.target.namevalue })} namevalue={this.state.namevalue} />
                    <FormControl type="text" className="bg-light"  placeholder="Preis" onChange={e => this.setState({ value2: e.target.pricevalue })} pricevalue={this.state.pricevalue} />
                    <Button className="inputField" variant="secondary" onClick={() => {this.addTableContent()}}>hinzufügen</Button>
                </InputGroup>
                <Table striped bordered hover variant="dark">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Preis</th>
                        </tr>
                    </thead>
                    <tbody>
                    {
                        this.state.incomes_or_spendings.map((income_or_spending) => (<InsertTable title={income_or_spending.title} content={income_or_spending.amount} />))
                    }
                    </tbody>
                </Table>
            </Accordion.Body>
        </Accordion.Item>
    );
}

class InsertTable extends Component{

    constructor(props) {
        super(props);

        this.state = {
           
            title: props.title,
            content: props.content,
  
        }
    }

    render() {
        return(
         <>
              

              <tr>
                
                <td>{this.state.title}</td>
                <td>{this.state.content}</td>
                
              </tr>

              </>
        );
    }

}
