import { useRef, useState } from 'react'
import { useHistory } from 'react-router-dom'
import { Container, Navbar, Nav, Form, Button } from 'react-bootstrap';
import { useAuth } from '../components/Auth'

export function Signup() {
  const emailRef = useRef()
  const passwordRef = useRef()
  const passwordConfRef = useRef()

  const [error, setError] = useState(null)

  const { signUp } = useAuth()
  const history = useHistory()

  async function handleSubmit(e) {
    e.preventDefault()

    const email = emailRef.current.value
    const password = passwordRef.current.value
    const passwordConf = passwordConfRef.current.value


    if(password === passwordConf){
      const { error } = await signUp({ email, password, })
      if (error) {
        alert('error signing up')
      } else {
        history.push('/overview')
      }
    } else {
      alert('Passworte stimmen nicht Ã¼berein')
    }
  }

  return (
      <>
        <Navbar bg="dark" variant="dark">
          <Container>
            <Navbar.Brand href="#home">Navbar</Navbar.Brand>
            <Nav className="me-auto">
              <Nav.Link href="#predictor">Predictor</Nav.Link>

            </Nav>
          </Container>
        </Navbar>


        <Container>
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3 mt-3" controlId="email">
              <Form.Label>Email Adresse eingeben</Form.Label>
              <Form.Control type="email" placeholder="name@example.com" ref={emailRef} />
            </Form.Group>
            <Form.Group className="mb-3" controlId="password">
              <Form.Label>Passwort eingeben</Form.Label>
              <Form.Control type="password" placeholder="Password" ref={passwordRef} />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formPlainTextPassword">
              <Form.Label>Passwort nochmals eingeben</Form.Label>
              <Form.Control type="password" placeholder="Passwort Confirmation" ref={passwordConfRef} />
            </Form.Group>

            <Form.Group>

              <Button variant="secondary" type="submit"  >Registrieren</Button>

            </Form.Group>
          </Form>
          <p>
            Already have an account? <Button variant="secondary" href='/login'  >Einloggen</Button>
          </p>
        </Container>
      </>
  )
}
