import { useRef, useState } from 'react'
import { useHistory, Link } from 'react-router-dom'

import { useAuth } from '../components/Auth'

export function ff() {
  return(
    <p>Hallo</p>
  )
}

export function Signup() {
  const emailRef = useRef()
  const passwordRef = useRef()
  const passwordConfRef = useRef()

  const [error, setError] = useState(null)

  const { signUp } = useAuth()
  const history = useHistory()

  async function handleSubmit(e) {
    e.preventDefault()

    const email = emailRef.current.value
    const password = passwordRef.current.value
    const passwordConf = passwordConfRef.current.value

    if(password === passwordConf){
      const { error } = await signUp({ email, password, })
      if (error) {
        alert('error signing up')
      } else {

        history.push('/')
      }
    } else {
      alert('Passworte stimmen nicht überein')
    }
  }

  return (
    <>
      <form onSubmit={handleSubmit}>
        <div>{error && JSON.stringify(error)}</div>

        <label htmlFor="input-email">Email</label>
        <input id="input-email" type="email" ref={emailRef} />

        <label htmlFor="input-password">Password</label>
        <input id="input-password" type="password" ref={passwordRef} />

        <label htmlFor="input-password">Password Confirmation</label>
        <input id="input-passwordConfirmation" type="password" ref={passwordConfRef} />

        <br />

        <button type="submit">Sign up</button>
      </form>

      <br/>

      <p>
        Already have an account? <Link to="/login">Log In</Link>
      </p>
    </>
  )
}
