import {Component, React, useRef} from 'react';
import { supabase } from '../components/supabaseClient'
import 'bootstrap/dist/css/bootstrap.min.css'
import 'bootstrap/dist/js/bootstrap.bundle.min.js'

import { Container, Row, Col, Accordion, Table, Button, InputGroup, FormControl } from 'react-bootstrap';

export class FinancialOverview extends Component {
    constructor(props) {
        super(props);
        this.state = {value: '', income_forms: [], spending_forms: [], incomes: [], spendings: []};
    }


    async createCategory(title) {
        const { data, error } = await supabase
            .from('income_forms')
            .insert([
                { title: title, user_id: supabase.auth.user().id }
            ])

        this.componentDidMount();
    }

    async componentDidMount(){
        const user_id = supabase.auth.user().id

        const data_income_forms = await this.fetchIncomeForms_forUser(user_id)
        const data_incomes_array = await Promise.all(data_income_forms.map(async income_form => {
            await this.fetchIncomes_forIncomeForms(income_form.id)
        }))

        console.log(data_incomes_array)

        this.setState({income_forms: data_income_forms, incomes: data_incomes_array});
    }

    async fetchIncomeForms_forUser(user_id) {
        const { data, error } = await supabase
            .from("income_forms")
            .select()
        return data
    }

    async fetchIncomes_forIncomeForms(income_forms_id) {
        const { data, error } = await supabase
            .from("incomes")
            .select()
        return data
    }

    async fetchSpendingForms_forUser(user_id) {
        const { data, error } = await supabase
            .from("spending_forms")
            .select()
            .eq("user_id", user_id)
        return data
    }

    async fetchSpendings_forSpendingForms(spending_forms_id) {
        const { data, error } = await supabase
            .from("spendings")
            .select()
            .eq("spending_forms_id", spending_forms_id)
        return data
    }

    render() {
        return(
            <Container>
                <Row>
                    <Col>
                        <div className="toptext">
                            <h1 className="text-center">Vermögenseingabe</h1>
                        </div>
                    </Col>
                </Row>
                <Row >
                    <Col md={6}>
                        <div className="inTitle">
                            <h2 className="text-center">Einnahmen</h2>
                        </div>
                        <div className="inTitle">
                            <InputGroup>
                                <FormControl type="number" max={int.max} id="new_form_input" placeholder="Neue Kategorie" className="bg-light" />
                                <Button className="inputField" variant="secondary" onClick={() => {
                                    const input = document.getElementById("new_form_input")
                                    this.createCategory(input.value)
                                    input.value = ""
                                }}>Create Category</Button>
                            </InputGroup>
                        </div>
                        <Accordion>
                            {
                                this.state.income_forms.map(incomeform => <InsertAccoridon form={incomeform} amounts={this.state.incomes} />)
                            }
                        </Accordion>
                    </Col>
                </Row>
            </Container>
        );
    }
}

class InsertAccoridon extends Component{

    constructor(props) {
        super(props);
        this.state = {form: {}, amounts: []};

        this.state = {
            form: props.form,
            amounts: props.amounts
        }
    }

    render() {
        return(
            <Accordion.Item eventKey={this.state.form.id}>
                <Accordion.Header>{this.state.form.title}</Accordion.Header>
                <Accordion.Body>
                    <InputGroup>
                        <FormControl type="text" className="bg-light" placeholder="Name" onChange={e => this.setState({ value: e.target.namevalue })} namevalue={this.state.namevalue} />
                        <FormControl type="text" className="bg-light"  placeholder="Preis" onChange={e => this.setState({ value2: e.target.pricevalue })} pricevalue={this.state.pricevalue} />
                        <Button className="inputField" variant="secondary" onClick={() => {this.addTableContent()}}>hinzufügen</Button>
                    </InputGroup>
                    <Table striped bordered hover variant="dark">
                        <thead>
                        <tr>
                            <th>Name</th>
                            <th>Preis</th>
                        </tr>
                        </thead>
                        <tbody>
                        {
                            //this.state.amounts.map(income => <InsertTable income={income} />)
                        }
                        </tbody>

                    </Table>
                </Accordion.Body>
            </Accordion.Item>
        );
    }
}

class InsertTable extends Component{
    constructor(props) {
        super(props);
        this.state = {income: {}};

        this.state = {
            income: props.income
        }
    }

    render() {
        return(
            <tr>
                <td>{this.state.income.title}</td>
                <td>{this.state.income.amount}</td>
            </tr>
        );
    }
}
